/* kindeditor-editor.min.js Chen 2015-02-02 */
!function($){
    KindEditor.ready(function(K) {
        var editor1 = K.create('.text-editor', {
            cssPath : '../../sources/plugin/kindeditor/plugins/code/prettify.css',
            uploadJson : '../../sources/plugin/kindeditor/php/upload_json.php',
            fileManagerJson : '../../sources/plugin/kindeditor/php/file_manager_json.php',
            allowFileManager : true,
            afterCreate : function() {
                var self = this;
                K.ctrl(document, 13, function() {
                    self.sync();
                    K('.forms')[0].submit();
                });
                K.ctrl(self.edit.doc, 13, function() {
                    self.sync();
                    K('.forms')[0].submit();
                });
            }
        });
        prettyPrint();
    });
}(jQuery)